package COREJAVA;

public class dowhile {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//do-while meansone block of code will surely run and next is focused on boolean criteria to be satified

		
		int i = 20;
		
	do {
		
		System.out.println(i);
		i++;
		
	}
	while (i<30);
	
	}

}
